import React from "react";
import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import { useDispatch, useSelector } from "react-redux";
import { toggle } from "../store/index.js"; // Assuming your slice file is named cartSlice.js

const Cart = (props) => {
  const showing = useSelector((state) => state.showCart); // Assuming your state slice is named showCart
  const dispatch = useDispatch();

  const toggleCartHandler = () => {
    dispatch(toggle());
  };

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <button onClick={toggleCartHandler}>Toggle Cart</button>
      {showing && (
        <ul>
          <CartItem
            item={{ title: "Test Item", quantity: 3, total: 18, price: 6 }}
          />
        </ul>
      )}
    </Card>
  );
};

export default Cart;
