import { configureStore } from "@reduxjs/toolkit";

const initialState = { showCart: false };

const cartSlice = configureStore({
  name: "displayCart",
  initialState,
  reducers: {
    toggle: (state) => {
      state.showCart = !state.showCart;
    },
  },
});
const store = configureStore(cartSlice);
export default store;
