import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  showCart: false,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    toggleCart: (state) => {
      state.showCart = !state.showCart;
    },
  },
});
export const action = cartSlice.actions;
export default cartSlice;
