import { createSlice } from "@reduxjs/toolkit";

const initialState = { showCart: false };

const cartSlice = createSlice({
  name: "displayCart",
  initialState,
  reducers: {
    toggle: (state) => {
      state.showCart = !state.showCart;
    },
  },
});

export const action = cartSlice.actions;
export default cartSlice;
