const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      // your logic to add an item to the cart
    },
    removeItemFromCart(state, action) {
      // your logic to remove an item from the cart
    },
    // other reducer actions
  },
});
