// CartButton.js
import React from "react";
import classes from "./CartButton.module.css";
import { useDispatch, useSelector } from "react-redux";
import { action } from "../store/slice";

const CartButton = () => {
  const dispatch = useDispatch();
  const cartQuantity = useSelector((state) => state.slice.totalQuantity);

  const toggleCartHandler = () => {
    // Dispatching the toggleCart action
    dispatch(action.toggleCart());
  };

  return (
    <div>
      <button className={classes.button} onClick={toggleCartHandler}>
        <span>My Cart</span>
        <span className={classes.badge}>{cartQuantity}</span>
      </button>

      <div className={classes.cart}>{/* Content of the cart */}</div>
    </div>
  );
};

export default CartButton;
